    class Program
    {
        static void Main(string[] args)
        {
            string cameraResponse = "";
            string        camResp = "";

            int[] rawData = GetRawCorrectionValues(cameraResponse);


        }




        private static int[] GetRawCorrectionValues(string inputText)
        {
            int[] result = new int[2048];

            for (int i = 0; i < 2048; i++)
            {
                int decimalPlace_0 = inputText[i * 4 + 1] - '0';
                int decimalPlace_1 = inputText[i * 4 + 0] - '0';
                int decimalPlace_2 = inputText[i * 4 + 3] - '0';
                int decimalPlace_3 = inputText[i * 4 + 2] - '0';

                result[i] = (decimalPlace_3 << 12) + (decimalPlace_2 << 8) + (decimalPlace_1 << 4) + decimalPlace_0;
            }

            return result;
        }

    }
}
